import React, { Component } from 'react';
import Header from './Header';
import Textwrap from './Textwrap';
import List from './List';
import Filter from './Filter';
import Todo from './Todo';
import './App.css';

class App extends Component {
  id = 3;

  state = {
    showWhat: 'all',
    whatUdo: [
      {
        id:0,
        doSomething: 'test',
        state: false
      },
      {
        id:1,
        doSomething: 'complete test',
        state: true
      },
      {
        id:2,
        doSomething: 'list test',
        state: false
      }
    ],
    showList: []
  }

  handleCreate = (data) => {
    const { whatUdo } = this.state;
    this.setState({
      whatUdo: whatUdo.concat(Object.assign({}, data, {
        id:this.id++
      }))
    });
  }

  handleRemove = (id) => {
    const { whatUdo } = this.state;
    this.setState({
      whatUdo: whatUdo.filter( todo => todo.id !== id )
    });
  }

  handleComplete = (id,data) => {
    const { whatUdo } = this.state;
    this.setState({
      whatUdo: whatUdo.map(
        todo => todo.id === id
          ? {...todo, ...data}
          : todo
      )
    });
  }

  handleEdit = (id,data) => {
    const { whatUdo } = this.state;
    this.setState({
      whatUdo: whatUdo.map(
        todo => todo.id === id
          ? {...todo, ...data}
          : todo
      )
    });
  }
  
  filtering = (filter) => {
    this.setState({
      showWhat: filter
    });
  }
  

  filterShow = (whatToShow) => {
    this.setState({
      showWhat: whatToShow
    });
    this.props.onFilter(whatToShow);
  }

  render() {
    const { data } = this.state.whatUdo;

    this.complete = this.props.data.filter( todo => todo.state === true );
    this.doit = this.props.data.filter( todo => todo.state === false );

    const list = data.map(
      todo => (
        <Todo 
          key={todo.id}
          todo={todo} 
          onRemove={this.handleRemove}
          onComplete={this.handleComplete}
          onEdit={this.handleEdit} />
      )
    );

    // const listAll = list;
    // const listdoit = this.doit.map(
    //   todo => (
    //     <Todo
    //       key={todo.id}
    //       todo={todo} 
    //       onRemove={onRemove}
    //       onComplete={onComplete}
    //       onEdit={onEdit} />
    //   )
    // );
    // const listComplete = this.complete.map(
    //   todo => (
    //     <Todo
    //       key={todo.id}
    //       todo={todo} 
    //       onRemove={onRemove}
    //       onComplete={onComplete}
    //       onEdit={onEdit} />
    //   )
    // );

    return (
      <div className="App">
        <Header name="TO DO" />
        <Textwrap
          onCreate={this.handleCreate} />
        <List 
          data={this.state.whatUdo}
          onRemove={this.handleRemove}
          onComplete={this.handleComplete}
          onEdit={this.handleEdit}
          listItem={list} />
        <Filter
          onFilter={this.filterShow} />
      </div>
    );
  }
}

export default App;
