import React, { Component, Fragment } from 'react';
import Todo from './Todo';
import Filter from './Filter';

class List extends Component {

  static defaultProps = {
    showWhat: 'all',
    data: [],
    onRemove: () => console.warn('onRemove not defined'),
    onComplete: () => console.warn('onComplete not definde'),
    onFilter: () => console.warn('onFilter not definde')
  }

  constructor(props) {
    super(props);
    this.state = {
      showList: this.props.data,
      showWhat: 'all'
    }
    
    this.complete = this.props.data.filter( todo => todo.state === false );
    this.doit = this.props.data.filter( todo => todo.state === true );
  }

  filterShow = (whatToShow) => {
    this.setState({
      showWhat: whatToShow
    });
    this.props.onFilter(whatToShow);
  }

  render() {
    const { data, onRemove, onComplete, onFilter, showWhat, onEdit, onCreate } = this.props;
    // this.showWhat = this.state.showWhat;
    const { abotFilter } = this.state.showWhat;
    const list = data.map(
      todo => (
        <Todo 
          key={todo.id}
          todo={todo} 
          onRemove={onRemove}
          onComplete={onComplete}
          onEdit={onEdit} />
      )
    );

    // const listAll = list;
    // const listComplete = this.complete;
    // const listdoit = this.doit;

    const filter = this.state.showWhat;
console.log('show me what props showWhat : ', this.props.showWhat)
console.log('data : ', data);
console.log('filter : ', filter);
    const listNew = data.filter(
      todo => (
        <Todo
          key={todo.state}
          todo={todo} 
          onRemove={onRemove}
          onComplete={onComplete}
          onEdit={onEdit} />
      )
    );
    const listComplete = this.complete;
    const listdoit = this.doit;

    console.log('show me listNew : ', listNew);
    console.log('show me listComplete : ', listComplete);
    console.log('show me listdoit : ', listdoit);
    console.log('show me showWhat : ', this.state.showWhat);

    return (
      <Fragment>
        <div className="todoList_wrap">
          {
            list
            // (function() {
            //   if(filter === 'all') return (<div>{listAll}</div>);
            //   if(filter === 'complete') return (<div>{listComplete}</div>);
            //   if(filter === 'doit') return (<div>{listdoit}</div>);
            // })()
          }
        </div>
        <Filter
          onFilter={this.filterShow} />
      </Fragment>
    );
  }
}

export default List;
