import React, { Component, Fragment } from 'react';

class Todo extends Component {
  static defaultProps = {
    todo: {
      name: '뭐할건뎅?',
      id: 0,
      state: false
    }
  }

  constructor(props) {
    super(props);
    this.state = {
      edit: false,
      doSomething: '',
      state: false
    }
  }

  handleRemove = () => {
    const { todo, onRemove } = this.props;
    onRemove(todo.id);
  }

  handleComplete = () => {
    this.props.todo.state = !this.props.todo.state;
    const { onComplete } = this.props;
    onComplete( this.props.todo.id, this.props.todo );
  }

  handleChange = (e) => {
    this.setState({
      doSomething: e.target.value
    });
  }

  showEdit = () => {
    const { eidt } = this.state;
    this.setState({
      edit: !eidt
    });
  }

  handleEdit = (e) => {
    e.preventDefault();
    const { onEdit } = this.props;
    const { eidt } = this.state;
    this.setState({
      doSomething: '',
      edit: !eidt
    });
    onEdit( this.props.todo.id, this.state );
  }

  render() {
    const { doSomething } = this.props.todo;
    const { edit } = this.state;

    if( !edit ) {
      return (
        <Fragment>
          <div 
            className={ this.props.todo.state && !this.state.edit ? 'todo_item complete' : 'todo_item'} 
            onClick={!this.state.edit ? this.handleComplete : ''}>
            {
              this.state.edit !== false 
              ? ( 
                  <form onSubmit={this.handleEdit}>
                    <input 
                      type="text"
                      placeholder="뭐라고바꿀꼰데?!?!?"
                      onChange={this.handleChange}
                      value={this.state.doSomething} />
                    <button id="btn_add" type="submit">EDIT</button>
                  </form>
                ) 
              : doSomething
            }
          </div>
          {
            !this.props.todo.state && <div onClick={this.showEdit} className="edit"></div>
          }
          <div onClick={this.handleRemove} className="trash"></div>
        </Fragment>
      );
    }

    return (
      <Fragment>
        <div 
          className={ this.props.todo.state && !this.state.edit ? 'todo_item complete' : 'todo_item'} >
          { doSomething }
        </div>
        {
          !this.props.todo.state && <div onClick={this.showEdit} className="edit"></div>
        }
        <div onClick={this.handleRemove} className="trash"></div>
      </Fragment>
    );
  }
}

export default Todo;
