import React, { Component, Fragment } from 'react';

class Todo extends Component {
  static defaultProps = {
    todo: {
      name: '뭐할건뎅?',
      id: 0,
      state: false
    }
  }

  constructor(props) {
    super(props);
    this.state = {
      edit: false
    }
  }

  handleRemove = () => {
    const { todo, onRemove } = this.props;
    onRemove(todo.id);
  }

  handleComplete = () => {
    this.props.todo.state = !this.props.todo.state;
    const { todo, onComplete } = this.props;
    onComplete( this.props.todo.id, this.props.todo );
  }

  showEdit = () => {
    // this.setState({
    //   edit: !edit
    // });
  }

  handleEdit = () => {
    const { todo, onEdit } = this.props;
    onEdit( this.props.todo.id, this.props.todo );
  }

  render() {
    const {
      id, doSomething, state
    } = this.props.todo;

    return (
      <Fragment>
        <div className={ this.props.todo.state ? 'todo_item complete' : 'todo_item'} onClick={this.handleComplete}>
          {
            this.state.edit !== false ? (<div></div>) : doSomething
          }
        </div>
        <div onClick={this.showEdit} className="edit"></div>
        <div onClick={this.handleRemove} className="trash"></div>
      </Fragment>
    );
  }
}

export default Todo;
